// Generated by CoffeeScript 1.6.3
(function() {
  $(function() {
    var hostname;
    hostname = $('#hostname').val();
    $('#fire').click(function() {
      var input;
      input = $('#input').val();
      if (!input) {
        alert("Please input Markdown");
      }
      $('#progress').text('Processing...');
      $.ajax("http://" + hostname + "/markdown/raw", {
        type: "POST",
        data: input,
        success: function(res) {
          $('#output').val(res);
          return $('#progress').text('');
        }
      });
      return this;
    });
    return this;
  });

}).call(this);
